def parse(msg):
    type = msg[:16]
    time = msg[16:32]
    mac = msg[32:96]
    body = msg[96:96+256]
    iv = body[:32]
    enc_msg = body[32:]
    assert len(enc_msg) == 256-32
    return type, time, mac, iv, enc_msg    

