    enc_body = new_iv + enc_msg
    #print(enc_body)
    #print(enc_body.hex())

    mac = (hashlib.sha256(enc_body).hexdigest())
    #   print(mac)
    pkt = typ + timee + mac + enc_body.hex()
    splitM(pkt)
    return pkt

