import glob
import os
import time

def splitM(m):
    contt = m[:16]
    ts = m[16:2*16]
    print(f"content type: {contt}")
    print(f"timestamp: {ts}")
    mac = m[2*16:2*16+64]
    print(f"MAC: {mac}")
    hexenc = m[2*16+64:2*16+64+256]
    assert len(hexenc) == 256
    #print(f"AES CBC len: {len(hexenc)}")
    iv = hexenc[:32]
    hexenc = hexenc[32:]
    print(f"IV: {iv}")
    print(f"hexenc without IV: {hexenc}\n")

for filename in glob.glob(os.path.join("/home/isl/t2/share/Module4/", 'hello*.out')):
    print(filename)
    with open(os.path.join(os.getcwd(), filename), 'r') as f: # open in readonly mode
        hexstream = f.readline().rstrip()[132:] # 132 is the garbage before
        s = bytes.fromhex(hexstream).decode().rstrip().split('\n')
        #print(f"{s[-1]}\n")
        splitM(s[-1])

ts = str(int(time.time()*1000))+"$00"
assert len(ts) == 16
msg = f'hello$0000000000{ts}'
